import React, { useState } from 'react';
import { addStock } from "./services";

function AddStock({refreshStocks}){

    const [stock, setStock] = useState("");


    const handleChange = ((event) => {
        this.setState({value: event.target.value});
      });

    const handleinput  = ((stock) => {
        addStock(stock)
			.then( function(response) {
                setStock("");
                refreshStocks();
			})
			.catch(function(e) {
                console.log(e.message);
			});
    });

    return (
        <div >               
            <input type="text" name="text" value={stock} placeholder="Enter stock to add" onChange={this.handleChange}/>
            <button type="submit" onClick={handleinput}>Add</button>
        </div>
    );
}

export default AddStock;
