export const login = ({username}) => {
    return fetch('/login', {
      method: 'POST',
      headers: new Headers({ 'content-type': 'application/json' }),
      body: JSON.stringify({username}),
    })
    .catch( err => {
      return Promise.reject({ err: 'network-issue', message: "network error" });
    })
    .then( response => {
      if( response.ok ) {
        return response.json();
      }
      return Promise.reject({ err: 'server-error', message: "server error" });
    });
  };

export const addUser = ({username}) => {
    return fetch('/users', {
      method: 'POST',
      headers: new Headers({ 'content-type': 'application/json' }),
      body: JSON.stringify({username}),
    })
    .catch( err => {
      return Promise.reject({ err: 'network-issue', message: "network error" });
    })
    .then( response => { 
      if( response.ok ) {
        return Promise.resolve(response);
      }
      return Promise.reject({ err: 'server-error', message: "server error" });
    });
  };

export const addStock = ({stockname, username}) => {
    return fetch('/stocks', {
      method: 'POST',
      headers: new Headers({ 'content-type': 'application/json' }),
      body: JSON.stringify({stockname,username}),
      credentials: 'include',
    })
    .catch( err => {
      return Promise.reject({ err: 'network-issue', message: err });
    })
    .then( response => { 
      if( response.ok ) {
        return Promise.resolve(response);
      }
      return Promise.reject({ err: 'server-error', message: response });
    });
  };

export const getStocks = (() => {
    return fetch('/stocks', {
      method: 'GET', 
      credentials: 'include',
    })
    .catch( err => {
      return Promise.reject({ err: 'network-issue', message: err });
    })
    .then( response => {
      if( response.ok ) {
        return response.json();
      }
      return Promise.reject({ err: 'server-error', message: response.statusCode });
    });
  });

  export const refreshStocks = (() => {
    return fetch('/refreshstocks', {
      method: 'GET', 
      credentials: 'include',
    })
    .catch( err => {
      if(err)
        return Promise.reject({ err: 'network-issue', message: err});
      else
        return Promise.reject({ err: 'network-issue', message:"network error" });
    })
    .then( response => {
      if( response.ok ) {
        return response.json();
      }
      if(response.error){
        return Promise.reject({ err: 'server-error', message: response.error });
      }
      else
        return Promise.reject({ err: 'network-issue', message:"server error" });
    });
  });

export const updateStock = ({stockname, quantity, equity}) => {
    return fetch('/stocks', {
      method: 'PUT',
      headers: new Headers({ 'content-type': 'application/json' }),
      body: JSON.stringify({stockname, quantity, equity}),
      credentials: 'include',
    })
    .catch( err => {
      return Promise.reject({ err: 'network-issue', message: err });
    })
    .then( response => { 
      if( response.ok ) {
        return Promise.resolve(response);
      }
      return Promise.reject({ err: 'server-error', message: response });
    });
  };

