import React, { useState, useCallback, useEffect } from 'react';
import StockList from './StockList';
import {getStocks, getStocksFromAPI} from './services';
import AddStock from './AddStock'; 


function StockApp({onLogout, username}){

   const [stocks, setStocks] = useState([]);	
	const [errorMessage, setErrorMessage] = useState([]);

   const refreshStocks = useCallback(() => {
      getStocksFromAPI()
      .then( stocklist => { 
         setStocks(stocklist);
      })
      .catch(function(e){
         setErrorMessage(e.message);
      });
},[]);

   useEffect(() => {
      if(username){
      refreshStocks();
      }
   },[]);


	return (
   <div>
      <div>{errorMessage}</div>
      <div className="top-bar">
         <span>Logged in : {username}</span>
         <button type="submit" onClick={onLogout}>Logout</button>
      </div>
      <AddStock username={username} refreshStocks={refreshStocks}/>
      <StockList stocks={stocks}/>
   </div>
	);
};

export default StockApp;
