import React, { useState, useCallback, useEffect } from 'react';
import StockList from './StockList';
import {getStocks ,refreshStocks} from './services';
import AddStock from './AddStock'; 

const REFRESH_TIME_IN_MS = 60000;

function StockApp({onLogout, username}){

   const [stocks, setStocks] = useState([]);	
   const [errorMessage, setErrorMessage] = useState([]);

   const relaodStocks = useCallback(() => {
      getStocks()
      .then( stocklist => { 
         setStocks(stocklist);
      })
      .catch(function(err){
         console.log(err);
         setErrorMessage(err);
      });
   },[]);

   const handleRefreshStocks = useCallback(() => {
      refreshStocks()
      .then( () => { 
         relaodStocks();
      })
      .catch(function(err){
         setErrorMessage(err);
      });
   },[]);

   useEffect(() => {
      if(username){
         handleRefreshStocks();
			const intervalId = setInterval( () => {
				handleRefreshStocks();
			}, REFRESH_TIME_IN_MS );
			return function cleanup() {
				clearInterval(intervalId);
         };      
      }
   },[]);

	return (
   <div>
		<div><span id="errorMessage">{errorMessage}</span></div>
      <div className="top-bar">
         <span>Logged in : {username}</span>
         <button onClick={handleRefreshStocks}>Refresh</button>
         <button onClick={onLogout}>Logout</button>
      </div>
      <AddStock username={username} relaodStocks={relaodStocks}/>
      <StockList stocks={stocks} relaodStocks={relaodStocks}/>
   </div>
	);
};

export default StockApp;
