import React, { useState, useCallback, useEffect } from 'react';
import { updateStock } from './services';

function StockList({stocks, refreshStocks}){

   let [value, setValue] = useState(0);
   let [updateButtonDisabled, setUpdateButtonDisabled] = useState(true);

   const onKeyUp = (e) => {
      if(e.key === 'Enter') {
         console.log("Enter clicked")
         let index = e.target.id;
         let stockname = stocks[index]["stockName"];
         let quantity = stocks[index]["quantity"];
         let equity = stocks[index]["equity"];
         updateStock({stockname, quantity, equity})
         .then( () => { 
            refreshStocks();
         })
         .catch(function(e){
            //setErrorMessage(e.message);
            console.log("Error in updating stock");
         });
      }
  };

   const saveQuantityChanged = (e) => {
      let index = e.target.id;
      let stockname = stocks[index]["stockName"];
      let quantity = stocks[index]["quantity"];
      let equity = stocks[index]["equity"];
      updateStock({stockname, quantity, equity})
      .then( () => { 
         //refreshStocks();
      })
      .catch(function(e){
         //setErrorMessage(e.message);
         console.log("Error in updating stock");
      });
   };

   const handleQuantityChange = ((e) => {
      let quantityText = e.target.value;
      let index = e.target.id;
      let isnum = /^\d+$/.test(quantityText);
      if(isnum){
         let quantity = parseInt(quantityText);
         stocks[index]["quantity"] = quantity;
         let equity = stocks[index]["price"] * quantity;
         stocks[index]["equity"] = equity.toFixed(2);
         setUpdateButtonDisabled(false);
         //setValue(++value);
      }
   });

	return (
   <div>
   <h2>Your Virtual Portfolio</h2>
   <ol className="stock-list"> 

      <li className="stock-item-header">
         <div>
         <div>Stock Symbol</div>
         <div>Price</div>
         <div>Last Updated Date</div>
         <div>Quantity</div>
         <div>Your Equity</div>
      </div> 
      </li>
   {
    stocks.map((stockMap,index)=>{
      return  <li className="stock-item" key={index}>
                  <div>
                  <div>{stockMap["stockName"]}</div>
                  <div>{stockMap["price"]}</div>
                  <div>{stockMap["lastUpdatedDate"]}</div>
                  <div>
                     <input type="text" id={index} value={stockMap["quantity"]} 
                      onChange={ e => handleQuantityChange(e)}/>
                     <button id={index} disabled={updateButtonDisabled} onClick={putQuantityChanged}>Update</button>
                  </div>
                  <div>{stockMap["equity"]}</div>
                </div> 
             </li>
          })
       }
    </ol>
    </div>
	);
};

export default StockList;
