import React, { useState } from 'react';
import { addStock } from "./services";

function AddStock({username, refreshStocks}){

    const [stockname, setStockname] = useState("");
    const [errorMessage, setErrorMessage] = useState("");


    const handleChange = ((inputText) => {
        setStockname(inputText);
    });

    const handleinput  = (() => {
        addStock({stockname,username})
			.then( function(response) {
                setStockname("");
                setErrorMessage("");
                refreshStocks();
			})
			.catch(function(e) {
                setErrorMessage("Error in Adding Stock");
			});
    });

    return (
        <div className="to-add">               
            <input type="text" name="text" value={stockname} placeholder="Enter stock to add" onChange={ e => handleChange(e.target.value)}/>
            <span>{errorMessage}</span>
            <button type="submit" onClick={handleinput}>Add</button>
        </div>
    );
}

export default AddStock;
