import React, { useState, useCallback, useEffect } from 'react';

function StockList({stocks}){

   let [currentStocks, setCurrentStocks] = useState(stocks);
   let [value, setUpdateValue] = useState(0);

   // let quantities = [];
   // let stockNames = [];
   // let equities = [];

   // stocks.forEach((stockMap) => {
   //    stockNames.push(stockMap["stockName"]);
   //    quantities.push(stockMap["quantity"]);
   //    equities.push(stockMap["equity"]);
   // });

   const onKeyUp = (e) => {
      if(e.key === 'Enter') {
          console.log("Enter clicked")
          console.log(e.target.value);
          console.log(e.target.key);
      }
  };

   const handleQuantityChange = ((e) => {
      let quantityText = e.target.value;
      let index = e.target.key;
      let isnum = /^\d+$/.test(quantityText);
      if(isnum){
         console.log("Is a number");

         //quantities[e.target.key] = quantityText;
         //stockName = stockNames[index];
         //quantity = parseInt(quantityText);
         //equity = equities[];

         //onChange={ e => handleQuantityChange(e)} 

         let quantity = parseInt(quantityText);
         quantities[index] = quantity;
         console.log("new quantity:" + quantities[index]);
         setUpdateValue(value++);
      }
      else{
         console.log("Is not a number");
         //quantities[e.target.key] = quantities[e.target.key];
      }

   });

	return (
   <div>
   <h2>Your Virtual Portfolio</h2>
   <ol className="stock-list"> 

      <li className="stock-item-header">
         <div>
         <div>Stock Symbol</div>
         <div>Price</div>
         <div>Last Updated Date</div>
         <div>Quantity</div>
         <div>Your Equity</div>
      </div> 
      </li>
   {
    stocks.map((stockMap,index)=>{
      return  <li className="stock-item" key={index}>
                  <div>
                  <div>{stockMap["stockName"]}</div>
                  <div>{stockMap["price"]}</div>
                  <div>{stockMap["lastUpdatedDate"]}</div>
                  <div>
                     <input key={index} type="text" name="text" value={quantities[index]} 
                      onChange={ e => handleQuantityChange(e)} onKeyUp={onKeyUp}/>
                  </div>
                  <div>{stockMap["equity"]}</div>
                </div> 
             </li>
          })
       }
    </ol>
    </div>
	);
};

export default StockList;
